	@RequestMapping("/s")
	public @ResponseBody List<Book> searchBook(String className, String key) {
        return SearchFactory.create(bookRepository, className).search(key);
	}
	
    @RequestMapping("/addbook")
//    public @ResponseBody Book addBook(String bookName, int pos, Model model) {
  public @ResponseBody Book addBook(String bookName, int pos, Model model) {

    	Book book = bookRepository.findByBookNameLike("%" + bookName + "%").get(0);
    	model.addAttribute(book);
    	String username = httpServletRequest.getRemoteUser();
        User user = userRepository.findByName(username);
        Set<Book> userbooks;
        if (pos == 0) {
            userbooks = user.getReading_books();
            userbooks.add(book);
        } else if (pos == 1) {
            userbooks = user.getPrefer_books();
            userbooks.add(book);
        } else if (pos == 2) {
            userbooks = user.getHave_read_books();
            userbooks.add(book);
        } else {}
        
//        userRepository.save(Arrays.asList(user));
        userRepository.save(user);
        return book;
    }
