    @RequestMapping(value="/book/{id}")
    public String book(Model model, @PathVariable String id) {
        Book book = bookRepository.findOne(id);
        List<BookComment> bookComments = bookCommentRepository.findByBook(book);
        String file = "/images/" + book.getBookId() + ".jpg";
        model.addAttribute("book", book);
        model.addAttribute("file", file);
        model.addAttribute("bookComments", bookComments);
        return "book";
    }

